import "./post.css"
import React, {useEffect, useState} from "react";
import httpClient from "../../httpClient";
export default function Post({aSinglePost})
{
    const [comments,setComments] = useState([])
    const postID = aSinglePost.id
    useEffect( () => {
        (async () => {
            try{
                const listOfComments = await httpClient.post("//127.0.0.1:5000/comments", {
                    postID
                })
                setComments(listOfComments.data)
            }
            catch(e){
                console.log("no comments")
            }
        })();
    },[])

    return(
        <div className="post">

            <div className="postItem">
                <span className="postTitle">{aSinglePost.title}</span>
                <span className="postDate">Created by {aSinglePost.authorName}</span>
                <p className={"postContent"}>{aSinglePost.content}</p>
                <p className={"postDate"}>{aSinglePost.date}</p>

                {comments.map((comment) => {
                    return(
                        <span className={"postDate"}>
                            {comment.content}
                        </span>
                    )
                })}


            </div>
        </div>
    )
}
