import "./post.css"
import React, {useEffect, useState} from "react";
import httpClient from "../../httpClient";
import {Link} from "react-router-dom";
import * as http from "http";
export default function Post({aSinglePost})
{
    const [user,setUser] = useState( '')


    const [comments,setComments] = useState([])
    const postID = aSinglePost.id

    useEffect(()=>{
        (async () => {
            try {
                const resp = await httpClient.get("//127.0.0.1:5000/@me");
                setUser(resp.data);
            }
            catch (e){
                console.log("Not Authenticated.")
            }
        })();
    },[])
    useEffect( () => {
        (async () => {
            try{
                const listOfComments = await httpClient.post("//127.0.0.1:5000/comments", {
                    postID
                })
                setComments(listOfComments.data)
            }
            catch(e){
                console.log("no comments")
            }
        })();
    },[])
    let postId = aSinglePost.id

    const deletePost = async  () => {
        if (window.confirm("Delete?")) {
            await httpClient.post("//127.0.0.1:5000/deletepost",{
                postId
            });
            // window.location.reload(2);


        }
    }
    return(
        <div className="post">
            <div>
                <span className="postTitle" >{aSinglePost.title}</span>
                <span className="postAuthor">Created by {aSinglePost.authorName}</span>
                <p className={"postContent"}>{aSinglePost.content} </p>
                <p className={"postDate"}>{aSinglePost.date}</p>
                {aSinglePost.authorId == user.id ? (<ul className={"deletebtn list"}><i className="far fa-trash-alt " onClick={deletePost}></i>
                    <i className={" likes far fa-heart"}></i></ul>)
                    :
                    (<p ></p>)}
                {/*{aSinglePost.likes>0 ? (<p className={"likes far fa-heart "} > {aSinglePost.likes}</p>*/}
                {/*):(<p></p>)}*/}



                {comments.map((comment) => {
                    return(
                        <p className={"postDate"}>

                            {comment.content} by {comment.authorName}
                        </p>
                    )
                })}


            </div>
        </div>
    )
}
