import "./post.css"
import React, {useState} from "react";
import httpClient from "../../httpClient";
export default function Post({aSinglePost})
{
    const [userID,setUserID] = useState('')
const getUser = async ( ) =>{
    try {
        const resp = await httpClient.post("//127.0.0.1:5000/login", {
            userid
        });
        console.log(resp.data)
        window.location.href = "/home";

    } catch(error) {
        alert("INVALID!");

    }
}
    return(
        <div className="post">
            <form>
                <button type="button" className="signupButton" onClick={() => getUser()}>LOGIN</button>

            </form>
            <div className="postItem">
                <span className="postTitle">{aSinglePost.title}</span>
                <p className={"postContent"}>{aSinglePost.content}</p>
                <p className={"postDate"}>{aSinglePost.date}</p>
            </div>
        </div>
    )
}
