import { useState } from 'react';
import APIService from "./APIService";
import "./signup.css"

const Signup = (props) => {
    let errors = {};
    const [fullName, setFullname] = useState('')
    const [userName, setUsername] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    const insertUser = () => {
        APIService.InsertUser({fullName, userName, email, password})
            .then((response) => props.insertedUser(response))
            .catch(error => console.log('error', error))
    }

    const handleSubmit = (event) => {

        if(handleValidation()){
            event.preventDefault()
            console.log()
            insertUser()
            setFullname('')
            setUsername('')
            setEmail('')
            setPassword('')
            console.log("User Added, Success")
        }
        else{
            // alert("Form Has Errors.")
        }

    }
    const handleChange = (e) => {
        if(e.target.value.indexOf('@') === -1){
            errors['email'] = 'NO @';

        }
    }
    const handleValidation = (event) => {
        let validForm = false;
        if(fullName.length===0){
            errors['fullname'] = 'Length == 0';
            console.log(errors)
            console.log("Fullname length is 0");
        }

        return validForm;
    }
    // handleValidation.bind(this,fullName);
    return (
        <div className='signup'>
            <span className="signupTitle">GLAD YOU'RE WITH US!</span>

            <form onSubmit={handleSubmit} className="signupForm">
                <input type='text' required
                       placeholder='Full Name'
                       onChange={(e) => {setFullname(e.target.value); }}
                       value={fullName}
                       className='signupInput'/>
<span className={"errors"}>problem</span>
                <input type='text' required
                       placeholder='Username'
                       onChange={(e)=>setUsername(e.target.value)}
                       value={userName}
                       className='signupInput'/>

                <input type='text' required minLength={5}
                       placeholder='Email'
                       onChange={(e)=>setEmail(e.target.value)}
                       value={email}
                       className='signupInput'/>

                <input type='password' required
                       placeholder='Password'
                       onChange={(e)=>setPassword(e.target.value)}
                       value={password}
                       className='signupInput'/>

                <button
                        className='signupButton'
                        > SignUp
                </button>
            </form>

        </div>
    )

}


export default Signup;
