import { useState } from 'react';
import APIService from "./APIService";
import "./signup.css"

const Signup = (props) => {
    let errors = {};
    const [fullName, setFullname] = useState('')
    const [userName, setUsername] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    const insertUser = () => {
        APIService.InsertUser({fullName, userName, email, password})
            .then((response) => props.insertedUser(response))
            .catch(error => console.log('error', error))
    }

    const handleSubmit = (event) => {
        if(handleValidation()){
            event.preventDefault()
            console.log()
            insertUser()
            setFullname('')
            setUsername('')
            setEmail('')
            setPassword('')
            console.log("User Added, Success")
        }
        else{
            alert("Form Has Errors.")
        }

    }
    const handleValidation = (event) => {
        let validForm = false;
        if(event.fullName.length ==0){
            console.log("Fullname length is 0");
            validForm = true;
        }

        return validForm;
    }

    return (
        <div className='signup'>
            <span className="signupTitle">GLAD YOU'RE WITH US!</span>
//handleValidation.bind(this,fullName);
            <form onSubmit={handleSubmit} className="signupForm">
                <input type='text'
                       placeholder='Full Name'
                       onChange={(e) => setFullname(e.target.value)}
                       value={fullName}
                       className='signupInput'/>

                <input type='text'
                       placeholder='Username'
                       onChange={(e)=>setUsername(e.target.value)}
                       value={userName}
                       className='signupInput'/>
                <span style={{ color: "red" }}>{this.state.errors["name"]}</span>

                <input type='text'
                       placeholder='Email'
                       onChange={(e)=>setEmail(e.target.value)}
                       value={email}
                       className='signupInput'/>

                <input type='password'
                       placeholder='Password'
                       onChange={(e)=>setPassword(e.target.value)}
                       value={password}
                       className='signupInput'/>

                <button
                        className='signupButton'
                        > SignUp
                </button>
            </form>

        </div>
    )

}


export default Signup;
