    useEffect(()=>{
        fetch('http://localhost:5000/users',{
            'methods':'GET',
            headers : {
                'Content-Type':'application/json'
            }
        })
            .then(response => response.json())
            .then(response => setUsers(response))
            .catch(error => console.log(error))


    },[])

    const insertedUser = (user) =>{
        const new_users = [...users,user]
        setUsers(new_users)
    }

  return (
    <div className="App">
        <BrowserRouter>
      <TopBar/>
      <SideBar/>
      {/*<Signup insertedUser={insertedUser}/>*/}
      {/*<Posts listOfPosts={post}/>*/}
      {/*<Home/>*/}
      {/*<Single/>*/}
            <Login/>
      {/*<Write/>*/}

      {/*<CreatePost/>*/}
      <BotBar/>
            </BrowserRouter>

    </div>
  );
}

export default App;
