    const [posts,setPosts] = useState([])

    useEffect( () => {
        (async () => {
            try{
                const listOfPosts = await httpClient.get("//127.0.0.1:5000/posts"

                )
                setPosts(listOfPosts.data)
            }
            catch(e){
                console.log("no posts")
            }
        })();
    },[])

    const mid = Math.ceil(posts.length/2);

    const firstHalf = posts.splice(0,mid);
    const secondHalf = posts.splice(-mid);

    return(
<div >


    <div className={"padd"}>
        {secondHalf.map((post) => {
            return(
                <div key="{item} ">
                    <Post  aSinglePost={post}/>
                </div>
            )
        })}        </div>


</div>


    )
}
