import "./post.css"
import firstPhoto from "../photos/aaron-burden-QJDzYT_K8Xg-unsplash.jpg"
export default function Post({aSinglePost})
{
    return(
        <div className="post">
            <img className="postImg" img src={firstPhoto} alt="Girl in a jacket"/>
            <div className="postInfo">
                <div className="postCats">
                    {/*<span className="postCat">{aSinglePost.title}</span>*/}
                    <span className="postCat">{aSinglePost.subject}</span>
                </div>
                <span className="postTitle">{aSinglePost.title}</span>
                <hr/>
                <span className="postDate">1 Hour ago</span>
            </div>
            <p className="postDescr">{aSinglePost.content}</p>
        </div>
    )
}
