import "./write.css"
import {useEffect, useState} from 'react';
import APIService from "../APIService";
import httpClient from "../../httpClient";

const Write = (props) => {

    useEffect(()=>{
        (async () => {
            try {
                const resp = await httpClient.get("//127.0.0.1:5000/@me");
                setUser(resp.data);
            }
            catch (e){
                console.log("Not Authenticated.")
            }
        })();
    },[])

    const [user,setUser] = useState( '')
    const [title, setTitle] = useState('')
     const [content, setContent] = useState('')
    const [userid,setUserid] = useState('')

    const insertPost = () => {
        APIService.InsertPost({title,content,userid })
            .then((response) => props.insertedPost(response))
            .catch(error => console.log('error', error))
    }

    const handleSubmit= (event) =>{

        event.preventDefault()
        insertPost()
        // setImg('')
        setTitle('')
        setContent('')


    }

    return (
        <div className="write">

            <form className="writeForm" onSubmit={handleSubmit} >
                <p className="writeFormGroup writeInputTitle">CREATE:</p>

                <div className="writeFormGroup">
                    <input type="text" required minLength={5} maxLength={30}
                           placeholder="Title"
                           id="textInput"
                           value={title}
                           className="writeInput"
                           onChange={(e)=>setTitle(e.target.value)}

                           autoFocus={true}/>

                </div>

                <div className="writeFormGroup ">
                    <input  type="text" required minLength={5} maxLength={300}
                           placeholder="What's this about?"
                           className="resizedTextbox"
                           onChange={(e)=>{setContent(e.target.value);setUserid(user.id)}}
                           value={content}
                    />

                </div>


                <div className="writeFormGroup">

                    <button className="writeSubmit btn-success"
                            >POST</button>
                </div>
            </form>
        </div>
    )
}
export default Write;
